fn read_str_from_slice<T>(&T)
-> Result<(&str, &[u8]), DecodeStringError<, super::bytes::BytesReadError>>
where T: ?Sized + AsRef<[u8]>

enum BytesReadError {
    InsufficientBytes {
            expected: usize,
            actual: usize,
            position: u64,
        },
}

enum ValueReadError<E = Error> where E: RmpReadErr {
    InvalidMarkerRead(E),
    InvalidDataRead(E),
    TypeMismatch(Marker),
}

enum NumValueReadError<E = Error> where E: RmpReadErr {
    InvalidMarkerRead(E),
    InvalidDataRead(E),
    TypeMismatch(Marker),
    OutOfRange,
}

enum ValueWriteError<E = Error> where E: RmpWriteErr {
    InvalidMarkerWrite(E),
    InvalidDataWrite(E),
}
